package main

import (
	"fmt"
	"sort"
)

func threeSum(nums []int) [][]int {
	sort.Ints(nums)
	result := make([][]int, 0)

	for i := 0; i < len(nums)-2; i++ {
		//to skip duplicates continuously or iteratively
		if i > 0 && nums[i] == nums[i-1] {
			continue
		}

		//initiate the two pointer approach and loop from left and right till middle
		for left, right := i+1, len(nums)-1; left < right; {
			sum := nums[i] + nums[left] + nums[right]
			if sum == 0 {
				result = append(result, []int{nums[i], nums[left], nums[right]})
				left++
				right--

				//check duplicates and skip if there is
				for left < right && nums[left] == nums[left-1] {
					left++
				}

				//check duplicates and skip if there is
				for left < right && nums[right] == nums[right+1] {
					right--
				}

			} else if sum < 0 {
				left++
			} else {
				right--
			}
		}
	}
	return result
}

